INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('0bf0182f596a4c7a096698eabad56b02', '独自随风', 4, '', '位于兴城铁路疗养院北院内的核心地段，是融东洋格局和西欧风韵为一体的小型建筑群，小巧玲珑，工艺精萃。这座别墅始建于20世纪20年代，全部建筑面积2000多平方米，占地面积达5000多平方米。是一所面南背北的小建筑群，围绕一眼大温泉而建。设有门厅、前厅和后厅三层套院。', null);
