INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('32980c6c4c34e06f19387b44275de591', 'Jason', 4, '', '在没有菊花岛的时候，渤海里住着龙王三太子，他异常凶恶，不仅兴风作浪祸害人，每月还要吃一个童男童女，每年更要一个十七八岁的姑娘做老婆。人们对付三太子可是没法，只好搬家躲灾。', null);
