INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('3a964f778dab4b35c0f50194918c6abc', '享受人生', 5, '', '我们漫步在大海边上，放眼望去，大海的尽头只见一条水平线，天和海在那里交界，云和浪在那里汇集，此时让我感到世界好像静止了一样，整个海岸就我们几人，每个人都深情地望着大海，享受着离开喧嚣的城市，只有在这里的宁静。', null);
