INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('49747f90a0a6e5a620401f03dbff8115', '普罗旺斯上空', 3, '', '兴城 古城位于 辽宁 省 葫芦岛 市 兴城 境内，历史文化名城。古城始建于明宣德三年 （公元1428年），为 宁远 卫城，清代重修，改称 宁远 州城。城的四面正中皆有城门，门外有半圆形瓮城。城墙基砌青色条石，外砌大块青砖，内垒巨型块石，中间夹夯黄土。四角高筑炮台，突出于城角，用以架设红夷大炮。 兴城 古城是五百年前留下来的一份珍贵的历史遗产，是我国目前保存最完整的一座明代古城，它经历了570多年的风雨浸蚀和战争摧残，基本保持原貌。交通方便， 兴城 古城 永宁 门（西门）距 兴城 火车站200多米，步行即可到达。也可乘1路车到南关站点下车步行到南关商业街，从延辉门（南门）进入 兴城 古城观光。门票：25.00元开放时间：08:00~18:00', null);
