INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('4e737596d216405c7d5e6d625fa074c0', '浮年', 3, '', '一座，为“援剿之兵，惟祖大乐、祖宽所统辽丁为最劲，杀贼亦最多”的袁崇焕部将祖大乐所建忠贞胆智坊，雕刻骑马征战、俘敌、凯旋等浮雕；一座，是为吴三桂舅舅、援剿总兵官祖大寿修建的功勋坊—登坛骏烈坊。南北楹柱两副御制对联：“桓纠兴歌，国倚干城之重；丝纶锡宠，朝隆铭鼎之褒”和“松檟如新，庆善培于四世；琳琅有赫，贲永誉之千秋”。', null);
