INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('96a624aeb72d43222f0f0d9fd4ff5a3c', '猴赛雷道航', 3, '', '我是五月一号去的红海滩，从大连开车开了大概四个小时到的。红海滩边上有一条街有很多饭馆还不错。五月的时候海滩刚有一点红，很多地方还只是泥的样子。整个景点规划的个人觉得很好，有不同的景观。景区内有小火车，不过得等很长时间。不建议五月份去。当天还挂大风，是我经历过最大的风了…', null);
