INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('e4a9b79a9a1dd619dc3f4704b95b6659', '升紫烟', 4, '', '兴城古城内南大街（即延辉街），矗立着两座雄伟壮观的石坊，也就是祖氏石坊，又称“二道牌坊”，是明崇祯帝为表彰镇守辽西边关的大将祖大寿、祖大乐兄弟守卫明抗清的而建造的牌坊。两坊相距85米，青色花岗岩雕刻，四柱三间五楼式，单檐庑殿式屋顶，配以双龙、瑞兽、花卉等精美图案，两坊柱下左右八只石狮子造型逼真，昂首躬背，威武之气。', null);
