INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('ef66c3d53ae8a5b557dbb9c01b6ac22a', '小女子胖墩墩', 5, '', '时间：2018年10月5日。下午两点左右到景区，还没什么红海，几乎都被淹了。但是到3、4点多时，红海滩就露出来了，好美。还有辽宁舰形状的栈道，还有点小壮观，很有造型感！适合拍大片。重点是不要门票，免费的。有不少停车位，厕所修建的也还可以，给人感觉是个新景点。小车停车费10元也不贵，很值啊！', null);
INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('f012a8142024827199c71c379a1227f1', 'Jason', 4, '', '在没有菊花岛的时候，渤海里住着龙王三太子，他异常凶恶，不仅兴风作浪祸害人，每月还要吃一个童男童女，每年更要一个十七八岁的姑娘做老婆。人们对付三太子可是没法，只好搬家躲灾。', null);
