INSERT INTO travel.comments (id, userName, goal, publishDate, message, other) VALUES ('ff2ae8ae096a3087ff7ba2e0e3887189', '黑暗中的恶魔', 5, '', '作为四大古城之一的兴城古城可能不大为大家知道，这里作为明朝的边防要塞，当初被清军攻占后就一把火烧尽了，现在大家看到的是后来重建的，去了解下明清交战的历史还好，里面除了建筑和展馆外可以看的东西不算多，但是夜景还是挺令人惊艳的', null);